css.variables:
:root {
  /* Colors */
  --color-background: #0A0A0A;
  --color-surface: #141414;
  --color-surface-elevated: #1A1A1A;
  --color-text-primary: #FEFEFE;
  --color-text-secondary: #A0A0A0;
  --color-text-muted: #606060;
  --color-border: #2A2A2A;
  --color-accent: #D4BA8A;
  --color-accent-muted: #7C6F61;
  --color-chrome-highlight: #F0F0F0;
  --color-chrome-mid: #B0B0B0;
  --color-chrome-shadow: #505050;
  --color-pure-black: #000000;

  /* Typography */
  --font-primary: 'SF Pro Display', -apple-system, BlinkMacSystemFont, 'Segoe UI', system-ui, sans-serif;
  --font-mono: 'SF Mono', 'Fira Code', 'JetBrains Mono', ui-monospace, monospace;

  --text-display: 72px;
  --text-h1: 48px;
  --text-h2: 32px;
  --text-h3: 24px;
  --text-body: 18px;
  --text-body-sm: 16px;
  --text-caption: 14px;
  --text-overline: 12px;

  --font-thin: 200;
  --font-light: 300;
  --font-regular: 400;
  --font-medium: 500;

  --leading-tight: 1.0;
  --leading-snug: 1.1;
  --leading-normal: 1.2;
  --leading-relaxed: 1.3;
  --leading-loose: 1.6;

  --tracking-wide: 0.1em;
  --tracking-normal: 0.02em;
  --tracking-tight: 0em;

  /* Spacing */
  --space-xs: 4px;
  --space-sm: 8px;
  --space-md: 16px;
  --space-lg: 24px;
  --space-xl: 32px;
  --space-2xl: 48px;
  --space-3xl: 64px;
  --space-4xl: 96px;

  /* Layout */
  --container-max: 1200px;
  --container-narrow: 800px;
  --container-wide: 1440px;
  --section-py: 120px;
  --section-py-sm: 80px;
  --content-gap: 64px;

  /* Border Radius */
  --radius-none: 0px;
  --radius-sm: 4px;
  --radius-md: 8px;
  --radius-lg: 12px;
  --radius-xl: 16px;
  --radius-full: 9999px;

  /* Shadows */
  --shadow-sm: 0 1px 2px rgba(0, 0, 0, 0.4);
  --shadow-md: 0 4px 12px rgba(0, 0, 0, 0.5);
  --shadow-lg: 0 8px 32px rgba(0, 0, 0, 0.6);
  --shadow-glow: 0 0 40px rgba(212, 186, 138, 0.15);

  /* Motion */
  --ease-out: cubic-bezier(0.16, 1, 0.3, 1);
  --duration-fast: 150ms;
  --duration-normal: 300ms;
  --duration-slow: 500ms;
}
